<template>
  <div class="login-container">
    <h2>로그인</h2>
    <form @submit.prevent="handleLogin">
      <div>
        <label>아이디: <input v-model="username" required /></label>
      </div>
      <div>
        <label>비밀번호: <input type="password" v-model="password" required /></label>
      </div>
      <button type="submit">로그인</button>
      <p v-if="errorMessage" class="error">{{ errorMessage }}</p>
    </form>
    <router-link to="/register">회원가입</router-link>
  </div>
</template>

<script>
import api from "../api";

export default {
  name: "LoginForm",
  data() {
    return {
      username: "",
      password: "",
      errorMessage: "",
    };
  },
  methods: {
    async handleLogin() {
      this.errorMessage = "";
      try {
        const response = await api.post("/api/auth/login", {
          username: this.username,
          password: this.password,
        });
        const result = response.data;
        console.log("백엔드 응답:", result);

        if (result.data && result.data.accessToken) {
          console.log("로그인 성공 - 토큰:", result.data.accessToken);
          localStorage.setItem("accessToken", result.data.accessToken);
          localStorage.setItem("refreshToken", result.data.refreshToken);
          localStorage.setItem("userId", result.data.userId); // userId 추가
          console.log("저장된 accessToken 확인:", localStorage.getItem("accessToken"));
          console.log("저장된 refreshToken 확인:", localStorage.getItem("refreshToken"));
          console.log("저장된 userId 확인:", localStorage.getItem("userId"));

          // redirect 쿼리 확인 후 이동
          const redirect = this.$route.query.redirect || "/";
          this.$router.push(redirect);
        } else {
          this.errorMessage = result.message || "로그인에 실패했습니다.";
          console.error("로그인 실패:", result);
        }
      } catch (error) {
        const errorResponse = error.response?.data;
        this.errorMessage = errorResponse?.message || "로그인 중 오류가 발생했습니다.";
        console.error("로그인 실패:", errorResponse || error.message);
      }
    },
  },
};
</script>

<style scoped>
.login-container {
  max-width: 400px;
  margin: 50px auto;
  padding: 20px;
}
form {
  display: flex;
  flex-direction: column;
  gap: 15px;
}
button {
  padding: 10px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 5px;
}
button:hover {
  background-color: #0056b3;
}
.error {
  color: red;
  margin-top: 10px;
}
</style>
